// Simple Combat System
// Demonstrates combat mechanics with health, damage, and death

// Character information
component Character {
    name: string
    level: integer
}

// Health component
component Health {
    current: integer
    max: integer
}

// Attack capability
component Attack {
    damage: integer
    speed: float
}

// Current attack target
component Target {
    entity: id?
}

// Function to calculate damage with multiplier
fn calculate_damage(base: float, multiplier: float): float {
    return base * multiplier
}

// Main attack rule: apply damage and schedule next attack
rule attack_rule on DoAttack {
    // Only attack if we have a target
    if entity.Target.entity != null {
        let target = entity.Target.entity
        let damage = entity.Attack.damage

        // Apply damage to target
        target.Health.current -= damage

        // Schedule next attack based on attack speed
        let delay = 100 / entity.Attack.speed
        schedule [delay: delay] DoAttack {
            source: entity.id
        }
    }
}

// Death check: emit Death event when health drops to zero
rule death_check on DoAttack {
    if entity.Health.current <= 0 {
        schedule Death {
            target: entity.id
        }
    }
}

// Trackers for UI feedback
tracker Health on DoAttack
tracker Character on DoAttack
tracker Health on Death

// Cleric Skill Choices BCL
// Defines skill selection strategy for the Cleric class

module cleric_skills

// === SKILL TREE DEFINITIONS ===
// Tier 1 Skills (Level 2+)
// - heal: Restore health to target (15 mana)
// - smite: Holy damage to single enemy (10 mana)
// - bless: Increase ally damage (12 mana)

// Tier 2 Skills (Level 4+)
// - group_heal: Heal all allies (35 mana)
// - holy_shield: Absorb damage on ally (25 mana)
// - divine_light: Damage and heal (30 mana)

// Tier 3 Skills (Level 7+)
// - resurrection: Revive fallen ally (100 mana)
// - sanctuary: Make ally immune briefly (60 mana)
// - divine_storm: Massive holy AoE (80 mana)

// === HELPER FUNCTIONS ===

fn health_percentage(entity): float {
    return entity.Health.current / entity.Health.max
}

fn mana_percentage(entity): float {
    return entity.Mana.current / entity.Mana.max
}

fn can_cast(character, manaCost): boolean {
    return character.Mana.current >= manaCost
}

fn find_most_injured_ally(allies: list): id {
    let mostInjured = allies[0]
    let lowestPct = 1.0

    for ally in allies {
        let hp_pct = ally.Health.current / ally.Health.max
        if hp_pct < lowestPct {
            mostInjured = ally
            lowestPct = hp_pct
        }
    }

    return mostInjured.id
}

fn count_injured_allies(allies: list, threshold: float): number {
    let count = 0
    for ally in allies {
        let hp_pct = ally.Health.current / ally.Health.max
        if hp_pct < threshold {
            count = count + 1
        }
    }
    return count
}

// === SKILL CHOICE IMPLEMENTATION ===

// Called when cleric levels up and gains a skill point
choice fn select_skill_on_levelup(
    character: Character & Skills,
    currentLevel: number
): string {
    // At level 2, get heal first
    if currentLevel == 2 {
        return "heal"
    }

    // At level 3, get bless for damage support
    if currentLevel == 3 {
        return "bless"
    }

    // At level 4, get smite for offense
    if currentLevel == 4 {
        return "smite"
    }

    // At level 5, get group heal
    if currentLevel == 5 {
        return "group_heal"
    }

    // At level 6, get holy shield for protection
    if currentLevel == 6 {
        return "holy_shield"
    }

    // At level 7+, get resurrection
    if currentLevel >= 7 {
        return "resurrection"
    }

    return "heal"
}

// === COMBAT SKILL SELECTION ===

// Called each combat round to select which skill to use
choice fn select_combat_skill(
    character: Character & Skills & Health & Mana & Combat,
    allies: list,
    enemies: list
): string {
    let hp_pct = health_percentage(character)
    let mana_pct = mana_percentage(character)

    // Check for dead allies that need resurrection
    for ally in allies {
        if ally.Health.current <= 0 {
            if can_cast(character, 100) && character.Skills.skill4 == "resurrection" {
                return "resurrection"
            }
        }
    }

    // Emergency self-heal
    if hp_pct < 0.25 && can_cast(character, 15) {
        if character.Skills.skill1 == "heal" {
            return "heal"
        }
    }

    // Check how many allies need healing
    let injuredCount = count_injured_allies(allies, 0.6)

    // Multiple allies injured - use group heal
    if injuredCount >= 2 && can_cast(character, 35) {
        if character.Skills.skill3 == "group_heal" {
            return "group_heal"
        }
    }

    // Single ally critically injured - use single target heal
    for ally in allies {
        let ally_hp_pct = ally.Health.current / ally.Health.max
        if ally_hp_pct < 0.3 && can_cast(character, 15) {
            if character.Skills.skill1 == "heal" {
                return "heal"
            }
        }
    }

    // Tank is taking heavy damage - use holy shield
    for ally in allies {
        if ally.Character.class == "Warrior" {
            let ally_hp_pct = ally.Health.current / ally.Health.max
            if ally_hp_pct < 0.5 && can_cast(character, 25) {
                if character.Skills.skill3 == "holy_shield" {
                    return "holy_shield"
                }
            }
        }
    }

    // Everyone healthy - use offensive abilities
    if injuredCount == 0 {
        // Divine storm on multiple enemies
        if enemies.count >= 2 && can_cast(character, 80) {
            if character.Skills.skill4 == "divine_storm" {
                return "divine_storm"
            }
        }

        // Bless the highest damage ally
        if can_cast(character, 12) && character.Skills.skill2 == "bless" {
            return "bless"
        }

        // Smite for damage
        if can_cast(character, 10) && character.Skills.skill2 == "smite" {
            return "smite"
        }
    }

    // Moderate injuries - divine light for combo heal/damage
    if can_cast(character, 30) && character.Skills.skill3 == "divine_light" {
        return "divine_light"
    }

    // Low mana - conserve with basic attack
    return "basic_attack"
}

// === TARGET SELECTION FOR HEALING ===

choice fn select_heal_target(
    character: Character,
    allies: list
): id? {
    let critical_threshold = 0.3
    let heal_threshold = 0.7

    // First, find critically wounded
    for ally in allies {
        let hp_pct = ally.Health.current / ally.Health.max
        if hp_pct < critical_threshold {
            return ally.id
        }
    }

    // Then, find anyone needing healing
    for ally in allies {
        let hp_pct = ally.Health.current / ally.Health.max
        if hp_pct < heal_threshold {
            return ally.id
        }
    }

    // No one needs healing
    return null
}

// === TARGET SELECTION FOR ATTACKS ===

// Cleric attacks lowest threat enemy
choice fn select_attack_target(
    character: Character,
    enemies: list
): id {
    let target = enemies[0]
    let lowestThreat = 999999

    for enemy in enemies {
        let threat = enemy.Combat.damage * (enemy.Health.current / enemy.Health.max)
        if threat < lowestThreat {
            target = enemy
            lowestThreat = threat
        }
    }

    return target.id
}

// Aggressive Flee Strategy for Classic RPG
// Prioritizes speed - rarely flees, accepts death penalties

module aggressive_flee

// Helper: Count alive heroes
fn count_alive_heroes(party: list): integer {
    let count = 0

    for hero in party {
        if hero.Health.current > 0 {
            count += 1
        }
    }

    return count
}

// Aggressive flee decision - only flee when imminent TPK
// Accepts death penalties for faster clear times
// Best for: Strong builds, speedruns
// Expected: Few retreats (0-3), more deaths (5-10)
choice fn should_flee_from_battle(
    party: list,
    enemies: list,
    runStats: RunStats
): boolean {
    // Never flee if on cooldown
    if !runStats.canFlee {
        return false
    }

    let alive_count = count_alive_heroes(party)

    // Only flee if down to 1 hero (about to wipe)
    // Risk multiple deaths (50s each) to avoid retreat penalties (10s)
    // Net positive if you can win fights without dying
    if alive_count <= 1 {
        return true
    }

    return false
}

// Conservative Flee Strategy for Classic RPG
// Prioritizes survival - flees early to minimize death penalties

module conservative_flee

// Helper: Calculate average party health percentage
fn calculate_avg_party_health(party: list): float {
    let total_health = 0.0
    let total_max_health = 0.0

    for hero in party {
        total_health += hero.Health.current
        total_max_health += hero.Health.max
    }

    if total_max_health == 0 {
        return 0.0
    }

    return total_health / total_max_health
}

// Conservative flee decision - retreat at 40% avg health
// Minimizes death risk but incurs more flee penalties
// Best for: Difficult content, learning phase
// Expected: More retreats (10-15), fewer deaths (0-2)
choice fn should_flee_from_battle(
    party: list,
    enemies: list,
    runStats: RunStats
): boolean {
    // Never flee if on cooldown
    if !runStats.canFlee {
        return false
    }

    let avg_health = calculate_avg_party_health(party)

    // Conservative threshold - flee when average health drops below 40%
    // This leaves a safety margin before deaths occur
    if avg_health < 0.4 {
        return true
    }

    return false
}

// Mage Skill Choices BCL
// Defines skill selection strategy for the Mage class

module mage_skills

// === SKILL TREE DEFINITIONS ===
// Tier 1 Skills (Level 2+)
// - fireball: High damage single target spell (20 mana)
// - ice_shard: Moderate damage with slow effect (15 mana)
// - arcane_missiles: Fast casting multi-hit (10 mana)

// Tier 2 Skills (Level 4+)
// - blizzard: AoE damage to all enemies (40 mana)
// - mana_shield: Convert damage to mana drain (25 mana)
// - pyroblast: Very high single target damage (50 mana)

// Tier 3 Skills (Level 7+)
// - meteor: Massive AoE damage (80 mana)
// - time_warp: Increase party attack speed (60 mana)
// - arcane_explosion: Instant AoE around caster (45 mana)

// === HELPER FUNCTIONS ===

fn health_percentage(entity): float {
    return entity.Health.current / entity.Health.max
}

fn mana_percentage(entity): float {
    return entity.Mana.current / entity.Mana.max
}

fn can_cast(character, manaCost): boolean {
    return character.Mana.current >= manaCost
}

// === SKILL CHOICE IMPLEMENTATION ===

// Called when mage levels up and gains a skill point
choice fn select_skill_on_levelup(
    character: Character & Skills,
    currentLevel: number
): string {
    // At level 2, get fireball for damage
    if currentLevel == 2 {
        return "fireball"
    }

    // At level 3, get ice shard for utility
    if currentLevel == 3 {
        return "ice_shard"
    }

    // At level 4, get arcane missiles for mana efficiency
    if currentLevel == 4 {
        return "arcane_missiles"
    }

    // At level 5, get blizzard for AoE
    if currentLevel == 5 {
        return "blizzard"
    }

    // At level 6, get mana shield for survival
    if currentLevel == 6 {
        return "mana_shield"
    }

    // At level 7+, get meteor for ultimate damage
    if currentLevel >= 7 {
        return "meteor"
    }

    return "fireball"
}

// === COMBAT SKILL SELECTION ===

// Called each combat round to select which skill to use
choice fn select_combat_skill(
    character: Character & Skills & Health & Mana & Combat,
    allies: list,
    enemies: list
): string {
    let hp_pct = health_percentage(character)
    let mana_pct = mana_percentage(character)

    // Emergency: If health is low and we have mana shield, use it
    if hp_pct < 0.3 && mana_pct > 0.5 {
        if character.Skills.skill3 == "mana_shield" {
            return "mana_shield"
        }
    }

    // If we have meteor and enough mana, use it on groups
    if enemies.count >= 2 && can_cast(character, 80) {
        if character.Skills.skill4 == "meteor" {
            return "meteor"
        }
    }

    // If multiple enemies and we have blizzard
    if enemies.count >= 2 && can_cast(character, 40) {
        if character.Skills.skill3 == "blizzard" {
            return "blizzard"
        }
    }

    // High mana - use pyroblast or fireball
    if mana_pct > 0.6 {
        if can_cast(character, 50) && character.Skills.skill3 == "pyroblast" {
            return "pyroblast"
        }
        if can_cast(character, 20) && character.Skills.skill1 == "fireball" {
            return "fireball"
        }
    }

    // Medium mana - use ice shard
    if mana_pct > 0.3 && can_cast(character, 15) {
        if character.Skills.skill2 == "ice_shard" {
            return "ice_shard"
        }
    }

    // Low mana - use arcane missiles (efficient)
    if can_cast(character, 10) && character.Skills.skill2 == "arcane_missiles" {
        return "arcane_missiles"
    }

    // Very low mana - basic attack to conserve
    return "basic_attack"
}

// === TARGET SELECTION ===

// Mages prioritize low health targets to finish them off
choice fn select_attack_target(
    character: Character,
    enemies: list
): id {
    let target = enemies[0]
    let lowestHealth = 999999

    // Find enemy with lowest current health
    for enemy in enemies {
        if enemy.Health.current < lowestHealth {
            target = enemy
            lowestHealth = enemy.Health.current
        }
    }

    return target.id
}

// Party Configuration BCL
// Complete party setup for Classic RPG

module my_party

// === PARTY COMPOSITION ===

party {
