// === GLOBAL STRATEGY SETTINGS ===

strategy {
    // When to use potions
    potion_threshold: 0.3

    // When to flee - balanced strategy (30% avg party health)
    flee_threshold: 0.3

    // Priority: survival > dps > utility
    priority: "balanced"
}

// === FLEE DECISION LOGIC ===

// Helper: Calculate average party health percentage
fn calculate_avg_party_health(party: list): float {
    let total_health = 0.0
    let total_max_health = 0.0

    for hero in party {
        total_health += hero.Health.current
        total_max_health += hero.Health.max
    }

    if total_max_health == 0 {
        return 0.0
    }

    return total_health / total_max_health
}

// Helper: Count alive heroes
fn count_alive_heroes(party: list): integer {
    let count = 0

    for hero in party {
        if hero.Health.current > 0 {
            count += 1
        }
    }

    return count
}

// Helper: Check if healer is alive
fn has_alive_healer(party: list): boolean {
    for hero in party {
        if hero.Character.class == "Cleric" && hero.Health.current > 0 {
            return true
        }
    }
    return false
}

// Helper: Count alive enemies
fn count_alive_enemies(enemies: list): integer {
    let count = 0

    for enemy in enemies {
        if enemy.Health.current > 0 {
            count += 1
        }
    }

    return count
}

// Main flee decision - balanced strategy
// Flees when party health is low to avoid death penalties (50s vs 10s)
choice fn should_flee_from_battle(
    party: list,
    enemies: list,
    runStats: RunStats
): boolean {
    // Never flee if on cooldown
    if !runStats.canFlee {
        return false
    }

    let avg_health = calculate_avg_party_health(party)
    let alive_count = count_alive_heroes(party)
    let enemy_count = count_alive_enemies(enemies)
    let healer_alive = has_alive_healer(party)

    // Flee if average party health below 30% AND multiple enemies
    // Math: Preventing 3+ deaths (150s) worth fleeing (10s)
    if avg_health < 0.3 && enemy_count >= 3 {
        return true
    }

    // Flee if healer dead AND health below 50%
    if !healer_alive && avg_health < 0.5 {
        return true
    }

    // Flee if down to 1 hero
    if alive_count <= 1 {
        return true
    }

    return false
}

// === IMPORT CLASS-SPECIFIC STRATEGIES ===

import warrior_skills
import mage_skills
import rogue_skills
import cleric_skills

// === GLOBAL TARGET SELECTION ===

// Priority targeting based on enemy type
fn enemy_priority(enemy: Character & Enemy): number {
    // Bosses are high priority
    if enemy.Enemy.isBoss {
        return 100
    }

    // Higher tier enemies are more dangerous
    return enemy.Enemy.tier * 20
}

// Global target selection - focus fire on priority targets
choice fn select_party_target(
    allies: list,
    enemies: list
): id {
    let target = enemies[0]
    let highestPriority = 0

    for enemy in enemies {
        let priority = enemy_priority(enemy)

        // Add bonus for low health enemies (finish them off)
        let hp_pct = enemy.Health.current / enemy.Health.max
        if hp_pct < 0.3 {
            priority = priority + 50
        }

        if priority > highestPriority {
            target = enemy
            highestPriority = priority
        }
    }

    return target.id
}

// Rogue Skill Choices BCL
// Defines skill selection strategy for the Rogue class

module rogue_skills

// === SKILL TREE DEFINITIONS ===
// Tier 1 Skills (Level 2+)
// - backstab: High crit chance attack
// - poison_blade: DoT damage over time
// - quick_slash: Very fast attack

// Tier 2 Skills (Level 4+)
// - shadowstep: Teleport behind enemy, guaranteed crit
// - fan_of_knives: AoE damage to all enemies
// - evasion: Greatly increase dodge chance

// Tier 3 Skills (Level 7+)
// - assassinate: Instant kill on low HP targets
// - smoke_bomb: Become untargetable briefly
// - blade_flurry: Attack all enemies rapidly

// === HELPER FUNCTIONS ===

fn health_percentage(entity): float {
    return entity.Health.current / entity.Health.max
}

fn is_stealthed(character): boolean {
    return character.Buffs.hasteBonus > 0.5
}

// === SKILL CHOICE IMPLEMENTATION ===

// Called when rogue levels up and gains a skill point
choice fn select_skill_on_levelup(
    character: Character & Skills,
    currentLevel: number
): string {
    // At level 2, get backstab for crits
    if currentLevel == 2 {
        return "backstab"
    }

    // At level 3, get poison blade for sustained damage
    if currentLevel == 3 {
        return "poison_blade"
    }

    // At level 4, get quick slash for speed
    if currentLevel == 4 {
        return "quick_slash"
    }

    // At level 5, get shadowstep for burst damage
    if currentLevel == 5 {
        return "shadowstep"
    }

    // At level 6, get evasion for survival
    if currentLevel == 6 {
        return "evasion"
    }

    // At level 7+, get assassinate for execution
    if currentLevel >= 7 {
        return "assassinate"
    }

    return "backstab"
}

// === COMBAT SKILL SELECTION ===

// Called each combat round to select which skill to use
choice fn select_combat_skill(
    character: Character & Skills & Health & Combat,
    allies: list,
    enemies: list
): string {
    let hp_pct = health_percentage(character)

    // Emergency survival: Use evasion or smoke bomb
    if hp_pct < 0.25 {
        if character.Skills.skill3 == "evasion" {
            return "evasion"
        }
        if character.Skills.skill4 == "smoke_bomb" {
            return "smoke_bomb"
        }
    }

    // Check for assassinate opportunity
    if character.Skills.skill4 == "assassinate" {
        for enemy in enemies {
            let enemy_hp_pct = enemy.Health.current / enemy.Health.max
            if enemy_hp_pct < 0.15 {
                return "assassinate"
            }
        }
    }

    // Multiple enemies - use fan of knives or blade flurry
    if enemies.count >= 3 {
        if character.Skills.skill4 == "blade_flurry" {
            return "blade_flurry"
        }
        if character.Skills.skill3 == "fan_of_knives" {
            return "fan_of_knives"
        }
    }

    // Shadowstep for burst damage on single target
    if enemies.count == 1 {
        if character.Skills.skill3 == "shadowstep" {
            return "shadowstep"
        }
    }

    // Default rotation: backstab > poison > quick slash
    if character.Skills.skill1 == "backstab" {
        return "backstab"
    }
    if character.Skills.skill2 == "poison_blade" {
        return "poison_blade"
    }
    if character.Skills.skill2 == "quick_slash" {
        return "quick_slash"
    }

    return "basic_attack"
}

// === TARGET SELECTION ===

// Rogues prioritize squishy high-damage targets (mages, healers)
choice fn select_attack_target(
    character: Character,
    enemies: list
): id {
    let target = enemies[0]
    let highestPriority = 0

    for enemy in enemies {
        let priority = 0

        // Prioritize low defense targets
        if enemy.Combat.defense < 5 {
            priority = priority + 50
        }

        // Prioritize high damage targets
        priority = priority + enemy.Combat.damage

        // Prioritize low health targets
        let hp_pct = enemy.Health.current / enemy.Health.max
        if hp_pct < 0.3 {
            priority = priority + 100
        }

        if priority > highestPriority {
            target = enemy
            highestPriority = priority
        }
    }

    return target.id
}

// Warrior Skill Choices BCL
// Defines skill selection strategy for the Warrior class

module warrior_skills

// === SKILL TREE DEFINITIONS ===
// Tier 1 Skills (Level 2+)
// - power_strike: Increased damage on next attack
// - shield_bash: Stuns enemy briefly
// - defensive_stance: Increases defense

// Tier 2 Skills (Level 4+)
// - cleave: Attack hits multiple enemies
// - battle_cry: Increases party damage
// - iron_skin: Temporary damage reduction

// Tier 3 Skills (Level 7+)
// - execute: High damage to low HP enemies
// - rally: Heal and buff nearby allies
// - berserker_rage: Massive damage but lower defense

// === HELPER FUNCTIONS ===

fn health_percentage(character): float {
    return character.Health.current / character.Health.max
}

fn has_skill_points(character): boolean {
    return character.Skills.skillPoints > 0
}

// === SKILL CHOICE IMPLEMENTATION ===

// Called when warrior levels up and gains a skill point
choice fn select_skill_on_levelup(
    character: Character & Skills,
    currentLevel: number
): string {
    // At level 2, choose first tier 1 skill
    if currentLevel == 2 {
        return "power_strike"
    }

    // At level 3, choose second tier 1 skill
    if currentLevel == 3 {
        return "shield_bash"
    }

    // At level 4, choose defensive stance
    if currentLevel == 4 {
        return "defensive_stance"
    }

    // At level 5, choose tier 2 skill - cleave for damage
    if currentLevel == 5 {
        return "cleave"
    }

    // At level 6, choose battle cry for party buff
    if currentLevel == 6 {
        return "battle_cry"
    }

    // At level 7+, choose tier 3 skill based on playstyle
    if currentLevel >= 7 {
        // Offensive warrior goes for execute
        return "execute"
    }

    return "power_strike"
}

// === COMBAT SKILL SELECTION ===

// Called each combat round to select which skill to use
choice fn select_combat_skill(
    character: Character & Skills & Health & Combat,
    allies: list,
    enemies: list
): string {
    let hp_pct = health_percentage(character)

    // If health is very low, use defensive abilities
    if hp_pct < 0.25 {
        if character.Skills.skill3 == "defensive_stance" {
            return "defensive_stance"
        }
        if character.Skills.skill2 == "shield_bash" {
            return "shield_bash"
        }
    }

    // If we have execute and enemy is low, use it
    if character.Skills.skill4 != "" {
        for enemy in enemies {
            let enemy_hp_pct = enemy.Health.current / enemy.Health.max
            if enemy_hp_pct < 0.2 {
                return "execute"
            }
        }
    }

    // If multiple enemies and we have cleave, use it
    if enemies.count >= 2 {
        if character.Skills.skill3 == "cleave" {
            return "cleave"
        }
    }

    // Default to power strike for damage
    if character.Skills.skill1 == "power_strike" {
        return "power_strike"
    }

    return "basic_attack"
}

// === TARGET SELECTION ===

// Warriors prefer to attack the strongest enemy
choice fn select_attack_target(
    character: Character,
    enemies: list
): id {
    let target = enemies[0]
    let highestDamage = 0

    // Find enemy with highest damage (threat)
    for enemy in enemies {
        if enemy.Combat.damage > highestDamage {
            target = enemy
            highestDamage = enemy.Combat.damage
        }
    }

    return target.id
}

